
public class ArithmeticImpl extends Arithmetic {

	@Override
	public int subtraction(int firstNo, int secondNo) {
		// TODO Auto-generated method stub
		int result= firstNo-secondNo;
		return result;
	}

	@Override
	public int subtraction(int firstNo, int secondNo, int thirdNo) {
		// TODO Auto-generated method stub
		int	result=firstNo-secondNo-thirdNo;
		return result;
		
	}

	@Override
	public double subtraction(double firstNo, double secondNo) {
		// TODO Auto-generated method stub
		double result=firstNo-secondNo;
		return result;
	}

	@Override
	public double subtraction(int firstNo, double secondNo) {
		// TODO Auto-generated method stub
		double result=firstNo-secondNo;
		return result;
	}

	@Override
	public float subtraction(float firstNo, float secondNo) {
		// TODO Auto-generated method stub
		float	result=firstNo-secondNo;
		return result;
	}
}


	
